<?php/** * @package Zifer Child */$zerif_bigtitle_background = get_theme_mod('zerif_bigtitle_background');if(!empty($zerif_bigtitle_background)):	echo '	<style>		section.big-title{			filter: progid:DXImageTransform.Microsoft.AlphaImageLoader(src=\''.$zerif_bigtitle_background.'\',sizingMethod=\'scale\');			background-image: url("'.$zerif_bigtitle_background.'");		}	</style>';endif;echo '<section class="big-title">	<div class="big-title-over">		<div class="container">';		$zerif_bigtitle_title = get_theme_mod('zerif_bigtitle_title',__('To add a title here please go to Customizer','zifer-child'));		$zerif_bigtitle_text = get_theme_mod('zerif_bigtitle_text',__('To add a text here please go to Customizer','zifer-child'));		if( !empty($zerif_bigtitle_title) ):			echo '<h1 class="intro-text">'.__($zerif_bigtitle_title,'zifer-child').'</h1>';		endif;		if( !empty($zerif_bigtitle_text) ):			echo '<span class="intro-content">'.__($zerif_bigtitle_text,'zifer-child').'</span>';		endif;		$zerif_bigtitle_bluebutton_label = get_theme_mod('zerif_bigtitle_bluebutton_label',__('First button','zifer-child'));		$zerif_bigtitle_bluebutton_url = get_theme_mod('zerif_bigtitle_bluebutton_url','#');		if ( !empty($zerif_bigtitle_bluebutton_label) && !empty($zerif_bigtitle_bluebutton_url) ):			echo '<div class="buttons">';			echo '<a href="'.$zerif_bigtitle_bluebutton_url.'" class="btn btn-primary custom-button blue-btn">'.__($zerif_bigtitle_bluebutton_label,'zifer-child').'</a>';			echo '</div>';		endif;	echo '		</div>		<div class="clear"></div>	</div></section>';?>